import {gql} from "@apollo/client";

export const GET_ALL_USERS = gql`
 query getAllUsers {
    getAllUsers {
      id
      name
      username
    }
  }

`;

export const LOGIN = gql`
query login(
    $username: String!
    $password: String!
  ) {
    login(
      username: $username
      password: $password
    ) {     
      message
    }
  }
`;
